% ForwardDifference.m% Approximating the derivative of exp(x) at x = 1 % by a finite difference formulaclose all;clc% Approximating the derivative of exp(x) at x = 1 % by a finite difference formulax = 1;f = @(x) exp(x); fdash = @(x) exp(x);disp(' ');disp('   i      h    finite difference    error');disp('-----------------------------------------');% exponent = 1:16;% h = 10.^(-exponent);exponent = 1:53;h = 2.^(-exponent);deriv = (f(x+h)-f(x))./h; %forward difference formulaerror = deriv - fdash(x);  % absolute error % print resultsfor i = 1:53  fprintf(' %2.0f   %4.2e  %14.6f   %10.3e \n',i,h(i),deriv(i),error(i));enddisp(' ');% plot resultsfigure(1)loglog(h,abs(error));xlabel('h'),ylabel('|error|');title('Total error in Forward Difference Formula');       